/* @flow */

import React, { Component } from 'react';
import {
  View,
  ScrollView,
  StyleSheet,
  Text,
  Clipboard,
  Platform,
  ToastAndroid,
  AlertIOS,
} from 'react-native';
import moment from 'moment';
import Share, { ShareSheet, Button } from 'react-native-share';
import { observer } from 'mobx-react/native';
import ActionBar from '../components/ActionBar';
import WebRender from '../components/WebRender';
import UserHead from '../components/UserHead';

@observer(['nav'])
export default class Content extends Component {

  constructor() {
    super();
    this.state = {
      visible: false,
    };
  }

  onCancel() {
    console.log('cancel');
    this.setState({ visible: false });
  }

  onOpen() {
    console.log('open');
    this.setState({ visible: true });
  }

  render() {
    const post = this.props.nav.content;
    const date = moment(post.date_gmt).format('ll');
    const shareOptions = {
      title: post.title.rendered,
      message: 'Read more at',
      url: post.link,
      subject: post.title.rendered,
    };
    return (
      <View style={styles.container}>
        <ActionBar content handleShare={() => Share.open(shareOptions)} />
        <ScrollView>
          <View style={[styles.scrollBg, styles.title]}>
            <Text style={styles.titletext}>{post.title.rendered}</Text>
          </View>
          <UserHead
            avatar_url={'http://www.phpclasses.org/browse/view/flash/file/64364/name/noavatar.jpg'}
            name={post.x_author}
            date={date}
          />
          <WebRender content={post.content.rendered} />
        </ScrollView>
        <ShareSheet visible={this.state.visible} onCancel={() => this.onCancel()}>
          <Button
            iconSrc={{ uri: TWITTER_ICON }}
            onPress={() => {
              this.onCancel();
              setTimeout(() => {
                Share.shareSingle(Object.assign(shareOptions, { social: 'twitter' }));
              }
              , 300);
            }}
          >Twitter</Button>
          <Button
            iconSrc={{ uri: FACEBOOK_ICON }}
            onPress={() => {
              this.onCancel();
              setTimeout(() => {
                Share.shareSingle(Object.assign(shareOptions, {
                  social: 'facebook',
                }));
              }, 300);
            }}
          >Facebook</Button>
          <Button
            iconSrc={{ uri: WHATSAPP_ICON }}
            onPress={() => {
              this.onCancel();
              setTimeout(() => {
                Share.shareSingle(Object.assign(shareOptions, {
                  social: 'whatsapp',
                }));
              }, 300);
            }}
          >Whatsapp</Button>
          <Button
            iconSrc={{ uri: GOOGLE_PLUS_ICON }}
            onPress={() => {
              this.onCancel();
              setTimeout(() => {
                Share.shareSingle(Object.assign(shareOptions, {
                  social: 'googleplus',
                }));
              }, 300);
            }}
          >Google +</Button>
          <Button
            iconSrc={{ uri: EMAIL_ICON }}
            onPress={() => {
              this.onCancel();
              setTimeout(() => {
                Share.shareSingle(Object.assign(shareOptions, {
                  social: 'email',
                }));
              }, 300);
            }}
          >Email</Button>
          <Button
            iconSrc={{ uri: CLIPBOARD_ICON }}
            onPress={() => {
              this.onCancel();
              setTimeout(() => {
                if (typeof shareOptions.url !== 'undefined') {
                  Clipboard.setString(shareOptions.url);
                  if (Platform.OS === 'android') {
                    ToastAndroid.show('Link copiado al portapapeles', ToastAndroid.SHORT);
                  } else if (Platform.OS === 'ios') {
                    AlertIOS.alert('Link copiado al portapapeles');
                  }
                }
              }, 300);
            }}
          >Copy Link</Button>
          <Button
            iconSrc={{ uri: MORE_ICON }}
            onPress={() => {
              this.onCancel();
              setTimeout(() => {
                Share.open(shareOptions);
              }, 300);
            }}
          >More</Button>
        </ShareSheet>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
  },
  scrollBg: {
    marginLeft: 10,
    marginRight: 10,
  },
  title: {
    padding: 0,
    marginTop: 10,
    marginBottom: 10,
  },
  titletext: {
    fontWeight: 'bold',
    fontSize: 25,
    color: '#000',
  },
  author: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 10,
  },
  avatar: {
    width: 40,
    height: 40,
    borderRadius: 50,
  },
  authorcontent: {
    padding: 5,
    flexDirection: 'column',
  },
});

//  twitter icon
const TWITTER_ICON = 'data:image/png;base64,';

//  facebook icon
const FACEBOOK_ICON = 'data:image/png;base64,';

//  whatsapp icon
const WHATSAPP_ICON = 'data:image/png;base64,';

//  gplus icon
const GOOGLE_PLUS_ICON = 'data:image/png;base64,';

//  email icon
const EMAIL_ICON = 'data:image/png;base64,';

//  clipboard icon
const CLIPBOARD_ICON = 'data:image/png;base64,';

//  more icon
const MORE_ICON = 'data:image/png;base64,';
